#if !USE_ASM
void Init_InterDeci(InDe_Struct *pInDe, UWORD inter, UWORD deci, UWORD len,
                    QWORD *pFilCoef, UWORD BufSizeIn, UWORD BufSizeOut)
{
    UWORD i;

    pInDe->inter      = inter;
    pInDe->deci       = deci;
    pInDe->len        = len;
    pInDe->BufSizeIn  = BufSizeIn;
    pInDe->BufSizeOut = BufSizeOut;
    pInDe->Offset     = 0;
    pInDe->Index      = 0;
    pInDe->Counter    = 0;
    pInDe->pCoef      = pFilCoef;

    for (i = 0; i < len; i++)
    {
        pInDe->K_buf[i] = 0;
    }
}
#else
void Init_InterDeci(InDe_Struct *pInDe, UWORD inter, UWORD deci, UWORD len,
                    QWORD *pFilCoef, UWORD BufSizeIn, UWORD BufSizeOut)
{
    UBYTE *pFilter = (UBYTE *)pInDe;
    UWORD i;

    for (i = 0; i < sizeof(InDe_Struct); i++)
    {
        *pFilter++ = 0;
    }

    pInDe->inter      = inter;
    pInDe->deci       = deci;
    pInDe->len        = len;
    pInDe->BufSizeIn  = BufSizeIn;
    pInDe->BufSizeOut = BufSizeOut;

    pInDe->Offset     = pInDe->K_buf;
    pInDe->pCoef      = pFilCoef;
}
#endif

#if !USE_ASM
void SampleRate_Change(InDe_Struct *pInDe, QWORD *SampleIn, QWORD *SampleOut)
{
    QDWORD qdOutput;
    QWORD  Offset;
    QWORD  Index;
    QWORD  len;
    QWORD  inter;
    QWORD  deci;
    QWORD  counter;
    QWORD  qOut;
    QWORD  *dline;
    QWORD  *dtemp;
    QWORD  *coef_ptr;
    QWORD  BufSizeIn;
    /* QWORD BufSizeOut; */
    UWORD i, j, k;

    j = 0;
    Offset = pInDe->Offset;
    len = pInDe->len;
    Index = pInDe->Index;
    inter = pInDe->deci;
    deci = pInDe->inter;
    counter = pInDe->Counter;
    BufSizeIn = pInDe->BufSizeIn;
    /* BufSizeOut = pInDe->BufSizeOut; */

    for (i = 0; i < BufSizeIn; i++)
    {
        dline             = &(pInDe->K_buf[Offset]);
        *dline            = SampleIn[i];
        dline[pInDe->len] = SampleIn[i];
        dline++;

        while ((Index < deci) && (counter == 0))
        {
            coef_ptr = (pInDe->pCoef) + (Index * len);

            /* form output accumulation */
            qdOutput = 0;
            dtemp = dline;

            for (k = 0; k < len; k++)
            {
                qdOutput += QQMULQD(*dtemp++, *coef_ptr++);
            }

            qOut = QDR15Q(qdOutput);

            SampleOut[j++] = qOut;
            Index += inter;
        }

        if (counter == 1)
        {
            counter = 0;
        }

        while (Index >= deci)
        {
            Index -= deci;

            if (Index >= deci)
            {
                counter = 1;
            }
        }

        Offset ++;

        if (Offset >= len)
        {
            Offset = 0;
        }
    }

    pInDe->Offset  = Offset;
    pInDe->Index   = Index;
    pInDe->Counter = counter;
}
#endif

